const axios = require("axios")

module.exports = {
  ax: axios.create({
    baseURL: "http://localhost:8081",
    timeout: 1000,
    headers: {
      // "Content-Encoding": "gzip",
    },
  }),

  /*
   * usage:
   *   function GetUser() {
   *     return mock(0, {
   *       name: "Alice",
   *     })
   *   }
   */
  mock(code, data) {
    return new Promise((resolve, reject) => {
      return resolve({
        code,
        data,
      })
    })
  },
}
